package netgithub1;

public class indexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String klm="Hayat çok güzelç";
		System.out.println(klm.indexOf("a"));
		System.out.println(klm.indexOf('y'));
		System.out.println(klm.indexOf("güzel"));
		System.out.println(klm.indexOf('ç',8));

	}

}
